import React from 'react'
const BackendErrorMessages=({backendError})=>{
    const errorMessage=Object.keys(backendError).map(name=>{
        const messages=backendError[name].join(' ')
        return `${name} ${messages}`
    })
return<ul className="error-messages">
    {
    errorMessage.map(errMsg=>{
        return <li key={errMsg}>{errMsg}</li>
    })}
</ul>
}
export default BackendErrorMessages