import React from 'react'
import { Link,NavLink } from 'react-router-dom'
import {CurrentUserContext} from 'context/currentUser'
import { useContext } from 'react'

const TopBar = () => {
    const[currentUser,] =useContext(CurrentUserContext)
    const userImage=(currentUser.isLoggedIn && currentUser.currentUser.image)|| 'https://via.placeholder.com/50'
    console.log('current User',currentUser);
    return (
        <nav className="navbar navbar-light">
            <div className="container">
                <Link to="/" className="navbar-brand">Curly Thoughts</Link>
                <ul className="nav navbar-nav pull-xs-right ">
                    <li className="nav-item ">
                        <NavLink to="/" className="nav-link ">
                        Home
                    </NavLink>
                    </li>
                    {currentUser.isLoggedIn&&(
                        <>
                        <li className="nav-item ">
                    <NavLink to="/articles/new" className="nav-link ">
                      <i className="ion-compose">New Post</i>
                    </NavLink>
                    </li>
                        <li className="nav-item ">
                    <NavLink to="/settings" className="nav-link ">
                      <i className="ion-gear-a">Settings</i>
                    </NavLink>
                    </li>
                        <li className="nav-item ">
                    <NavLink to={`/profiles/${currentUser.currentUser.username}`} className="nav-link ">
                      <img className="user-pic" src={userImage} alt='' />
                      {currentUser.currentUser.username}
                    </NavLink>
                    </li>
                        </>
                    )}
                    {currentUser.isLoggedIn===false &&(<>
                        <li className="nav-item "><NavLink to="/login" className="nav-link">
                        Sign In
                    </NavLink>
                    </li>
                    <li className="nav-item"><NavLink to="/register" className="nav-link">
                       Sign Up
                    </NavLink>
                    </li>
                    </>)}
                    
                    </ul>            
            </div>
        </nav>
    )
}

export default TopBar
