import React,{ useState } from 'react'
import { Link } from 'react-router-dom'
import useFetch from 'hooks/useFetch'
const Authentication = (props) => {

    const isLogin = props.match.path === '/login'
    const pageTitle = isLogin ? 'Sign In' : 'Sign Up'
    const descriptionLink = isLogin ? '/register' : '/login'
    const descriptionText = isLogin ? 'Need an Account ?' : 'Have an Account ?'
    const apiUrl = isLogin ? '/users/login' : '/users'


    const [username, setUserName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')


    const [{ isLoading, error, response }, doFetch] = useFetch(apiUrl)
    console.log('useFetch', isLoading, error, response)


    const handleSubmit = (e) => {
        e.preventDefault()
        const user=isLogin?{email,password}:{email,password,username}
        doFetch({
            method: 'post',
            data: {
                user
            }
        })
    }

    return (
        <div className="auth-page">
            <div className="container page">
                <div className="row">
                    <div className="col-md-6 offset-md-3 col-xs-12">
                        <h1 className="text-xs-center">{pageTitle}</h1>
                        <p className="text-xs-center">
                            <Link to={descriptionLink}>{descriptionText}</Link>
                        </p>
                        <form onSubmit={handleSubmit}>

                            <fieldset>
                                {!isLogin&&<fieldset className="form-group">
                                    <input value={username} onChange={e => setUserName(e.target.value)} type="text" className="form-control form-control-lg" placeholder="username" />
                                </fieldset>
                                }
                                <fieldset className="form-group">
                                    <input value={email} onChange={e => setEmail(e.target.value)} type="email" className="form-control form-control-lg" placeholder="email" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <input value={password} onChange={e => setPassword(e.target.value)} type="password" className="form-control form-control-lg" placeholder="password" />
                                </fieldset>
                                <button disabled={isLoading} className="btn btn-lg btn-primary">
                                    {pageTitle}
                                </button>
                            </fieldset>
                        </form>
                    </div>
                </div>
            </div>

        </div>
    )
}

export default Authentication
